CREATE SCHEMA IF NOT EXISTS `godog` DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci;
use `godog`;

CREATE TABLE IF NOT EXISTS `godog`.`members` (
  `id` VARCHAR(100) NOT NULL,
  `pwd` VARCHAR(100) NOT NULL,
  `name` VARCHAR(100) NOT NULL,
  `email` VARCHAR(1000) NULL DEFAULT NULL,
  `token` VARCHAR(10000) NULL DEFAULT NULL,
  PRIMARY KEY (`id`))
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4
COLLATE = utf8mb4_0900_ai_ci;

CREATE TABLE IF NOT EXISTS `godog`.`board` (
  `articleno` INT NOT NULL AUTO_INCREMENT,
  `id` VARCHAR(16) NULL DEFAULT NULL,
  `subject` VARCHAR(100) NULL DEFAULT NULL,
  `content` VARCHAR(2000) NULL DEFAULT NULL,
  `hit` INT NULL DEFAULT 0,
  `regtime` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`articleno`),
  INDEX `board_to_member_fk` (`id` ASC) VISIBLE,
  CONSTRAINT `board_to_member_fk`
    FOREIGN KEY (`id`)
    REFERENCES `godog`.`members` (`id`) on delete set null)
ENGINE = InnoDB
AUTO_INCREMENT = 1
DEFAULT CHARACTER SET = utf8mb4
COLLATE = utf8mb4_0900_ai_ci;

CREATE TABLE IF NOT EXISTS `godog`.`gangwoncode` (
  `accode` VARCHAR(30) NOT NULL,
  `acname` VARCHAR(30) NOT NULL,
  PRIMARY KEY (`accode`))
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4
COLLATE = utf8mb4_0900_ai_ci;

CREATE TABLE IF NOT EXISTS `godog`.`categorycode` (
  `pccode` VARCHAR(30) NOT NULL,
  `pcname` VARCHAR(30) NOT NULL,
  PRIMARY KEY (`pccode`))
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4
COLLATE = utf8mb4_0900_ai_ci;

CREATE TABLE IF NOT EXISTS `godog`.`tourlist` (
  `contentSeq` INT NOT NULL,
  `accode` VARCHAR(30) NOT NULL,
  `areaName` VARCHAR(30) NULL DEFAULT NULL,
  `pccode` VARCHAR(30) NOT NULL,
  `partName` VARCHAR(30) NULL DEFAULT NULL,
  `title` VARCHAR(100) NOT NULL,
  `address` VARCHAR(100) NULL DEFAULT NULL,
  `latitude` VARCHAR(100) NULL DEFAULT NULL,
  `longitude` VARCHAR(100) NULL DEFAULT NULL,
  `tel` VARCHAR(100) NULL DEFAULT NULL,
  PRIMARY KEY (`contentSeq`),
  FOREIGN KEY (`accode`) REFERENCES `godog`.`gangwoncode` (`accode`) on delete cascade,
  FOREIGN KEY (`pccode`) REFERENCES `godog`.`categorycode` (`pccode`) on delete cascade)  
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4
COLLATE = utf8mb4_0900_ai_ci;

CREATE TABLE IF NOT EXISTS `godog`.`bookmark` (
	  `bookmark_no` INT NOT NULL AUTO_INCREMENT,
	  `id` VARCHAR(100) NOT NULL,
	  `contentSeq` INT NOT NULL,
  PRIMARY KEY (`bookmark_no`),
  INDEX `bookmark_to_members_id_fk` (`id` ASC) VISIBLE,
  CONSTRAINT `bookmark_to_members_id_fk`
    FOREIGN KEY (`id`)
    REFERENCES `godog`.`members` (`id`),
    FOREIGN key (`contentSeq`)
    REFERENCES `godog`.`tourlist` (`contentSeq`))
ENGINE = InnoDB
AUTO_INCREMENT = 1
DEFAULT CHARACTER SET = utf8mb4
COLLATE = utf8mb4_0900_ai_ci;







